    try {
      const response = await axios.get('http://localhost:5000/getNotes');
      return response.data;
    } catch (error) {
      console.error(error);
    }
  };
  useEffect(() => {
    getNotes().then((data) => {
      setNotes(data);
      console.log("notes aren't " + notes); // Log the notes after they are fetched
